<script setup lang="ts">
import { useUserStore } from 'src/stores/userStore';
import { onMounted, reactive } from 'vue';

// Define interfaces for user data and achievements
interface Achievement {
    id: number;
    title: string;
    description: string;
    date: string;
    icon: string;
    color: string;
}

interface TeamMember {
    id: number;
    name: string;
    role: string;
    avatar: string;
}

interface UserProfile {
    id: string;
    first_name: string;
    last_name: string;
    email: string;
    avatar: string;
    status: string;
    role?: string;
    joinDate?: string;
    lastActive: string;
    achievements?: Achievement[];
    team?: TeamMember[];
    workHours?: {
        total: number;
        thisWeek: number;
    };
}

const userStore = useUserStore();

// Reactive state for user profile with demo details
const userProfile = reactive<UserProfile>({
    id: '',
    first_name: '',
    last_name: '',
    email: '',
    avatar: '',
    status: '',
    role: '',
    lastActive: '',
});

const fetchUserData = () => {
    const user = userStore.profileUser

    if (user) {
        Object.assign(userProfile, {
            id: user.id,
            first_name: user.first_name || '',
            last_name: user.last_name || '',
            email: user.email,
            avatar: user.avatar || 'data:image/jpeg;base64,',
            status: user.status || '',
            role: user.role || '',
            lastActive: user.last_access,
        });
    }
}

// Status color mapping
const getStatusColor = (status: string) => {
    switch (status) {
        case 'active': return 'positive';
        case 'inactive': return 'negative';
        case 'pending': return 'warning';
        default: return 'grey';
    }
};

onMounted(async () => {
    await userStore.fetchUserById();
    fetchUserData()
})
</script>

